export interface JWTInfo {
    iss: string;
    sub: string;
    aud: string;
    exp: number;
    iat: number;
}
export interface AccessInfo extends JWTInfo {
    jti: string;
    scp: string[];
    uid: string;
}
export interface IdInfo extends JWTInfo {
    nonce: string;
    auth_time?: number;
    acr?: string;
    amr?: string[];
    azp?: string;
}
